describe ProductTestsController do

  # This should return the minimal set of attributes required to create a valid
  # ProductTest. As you add validations to ProductTest, be sure to
  # update the return value of this method accordingly.
  def valid_attributes
    {}
  end

  describe "GET index" do
    it "assigns all product_tests as @product_tests" do
      product_test = ProductTest.create! valid_attributes
      get :index
      assigns(:product_tests).should eq([product_test])
    end
  end

  describe "GET show" do
    it "assigns the requested product_test as @product_test" do
      product_test = ProductTest.create! valid_attributes
      get :show, :id => product_test.id
      assigns(:product_test).should eq(product_test)
    end
  end

  describe "GET new" do
    it "assigns a new product_test as @product_test" do
      get :new
      assigns(:product_test).should be_a_new(ProductTest)
    end
  end

  describe "GET edit" do
    it "assigns the requested product_test as @product_test" do
      product_test = ProductTest.create! valid_attributes
      get :edit, :id => product_test.id
      assigns(:product_test).should eq(product_test)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new ProductTest" do
        expect {
          post :create, :product_test => valid_attributes
        }.to change(ProductTest, :count).by(1)
      end

      it "assigns a newly created product_test as @product_test" do
        post :create, :product_test => valid_attributes
        assigns(:product_test).should be_a(ProductTest)
        assigns(:product_test).should be_persisted
      end

      it "redirects to the created product_test" do
        post :create, :product_test => valid_attributes
        response.should redirect_to(ProductTest.last)
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved product_test as @product_test" do
        # Trigger the behavior that occurs when invalid params are submitted
        ProductTest.any_instance.stub(:save).and_return(false)
