        assigns(:product_test).should be_a_new(ProductTest)
      end

      it "re-renders the 'new' template" do
        # Trigger the behavior that occurs when invalid params are submitted
        ProductTest.any_instance.stub(:save).and_return(false)
        post :create, :product_test => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested product_test" do
        product_test = ProductTest.create! valid_attributes
        # Assuming there are no other product_tests in the database, this
        # specifies that the ProductTest created on the previous line
