      it "redirects to the product_test" do
        product_test = ProductTest.create! valid_attributes
        put :update, :id => product_test.id, :product_test => valid_attributes
        response.should redirect_to(product_test)
      end
    end

    describe "with invalid params" do
      it "assigns the product_test as @product_test" do
        product_test = ProductTest.create! valid_attributes
